"use strict";const{SSM}=require("@aws-sdk/client-ssm");exports.handler=async function(event){const props=event.ResourceProperties;if(console.info(`Reading function ARN from SSM parameter ${props.ParameterName} in region ${props.Region}`),event.RequestType==="Create"||event.RequestType==="Update"){const ssmParameter=await new SSM({region:props.Region}).getParameter({Name:props.ParameterName});return console.info("Response: %j",ssmParameter),{Data:{FunctionArn:ssmParameter.Parameter.Value}}}};
